                    <span className="text-sm text-purple-900">Business Builder</span>
                    <Badge variant="secondary" className="bg-purple-100 text-purple-700 text-xs">Ravi S.</Badge>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/jobs.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Briefcase, Search, MapPin, Clock, DollarSign, Building, Users, Star, ExternalLink } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";

interface Job {
  id: string;
  title: string;
  company: string;
  companyLogo?: string;
  description: string;
  location: string;
  type: 'full-time' | 'part-time' | 'contract' | 'remote';
  experienceLevel: 'entry' | 'mid' | 'senior' | 'executive';
  salaryRange: string;
  skills: string[];
  benefits: string[];
  postedById: string;
  postedBy: {
    firstName: string;
    lastName: string;
    profileImageUrl?: string;
  };
  applicationsCount: number;
  isUrgent: boolean;
  isRemote: boolean;
  createdAt: string;
}

export default function Jobs() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedType, setSelectedType] = useState("");
  const [selectedLevel, setSelectedLevel] = useState("");

  const { data: jobs = [], isLoading } = useQuery<Job[]>({
    queryKey: ["/api/jobs", searchTerm, selectedLocation, selectedType, selectedLevel],
  });

  const { data: myApplications = [] } = useQuery<any[]>({
    queryKey: ["/api/jobs/my-applications"],
    enabled: !!user,
  });

  const applyMutation = useMutation({
    mutationFn: async (jobId: string) => {
      await apiRequest(`/api/jobs/${jobId}/apply`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      toast({
        title: "Application Submitted",
        description: "Your job application has been submitted successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/jobs"] });
      queryClient.invalidateQueries({ queryKey: ["/api/jobs/my-applications"] });
    },
  });

  const filteredJobs = jobs.filter(job => {
    const matchesSearch = !searchTerm || 
      job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.skills.some(skill => skill.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesLocation = !selectedLocation || job.location.toLowerCase().includes(selectedLocation.toLowerCase());
    const matchesType = !selectedType || job.type === selectedType;
    const matchesLevel = !selectedLevel || job.experienceLevel === selectedLevel;

    return matchesSearch && matchesLocation && matchesType && matchesLevel;
  });

  const hasApplied = (jobId: string) => {
    return myApplications.some(app => app.jobId === jobId);
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="text-center space-y-2">
        <div className="flex items-center justify-center gap-2">
          <Briefcase className="h-8 w-8 text-blue-600" />
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
            Job Portal
          </h1>
        </div>
        <p className="text-gray-600 dark:text-gray-400">
          Discover career opportunities within our trusted community network
        </p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{jobs.length}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">Active Jobs</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-green-600">{jobs.filter(j => j.isRemote).length}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">Remote Jobs</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-purple-600">{jobs.filter(j => j.isUrgent).length}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">Urgent Hiring</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-orange-600">{myApplications.length}</div>
            <div className="text-sm text-gray-600 dark:text-gray-400">My Applications</div>
          </CardContent>
        </Card>
      </div>

      {/* Post Job Button */}
      {user && (
        <Card className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Building className="h-5 w-5 text-blue-600" />
                <div>
                  <p className="font-medium">Hiring for your company?</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    Post job openings and find talented community members
                  </p>
                </div>
              </div>
              <Button className="bg-blue-600 hover:bg-blue-700" data-testid="button-post-job">
                Post a Job
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Search and Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Search className="h-5 w-5" />
            Search Jobs
          </CardTitle>
          <CardDescription>
            Find opportunities that match your skills and preferences
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="search">Search</Label>
              <Input
                id="search"
                placeholder="Job title, company, skills..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                data-testid="input-search"
              />
            </div>

            <div>
              <Label htmlFor="location">Location</Label>
              <Input
                id="location"
                placeholder="City, State, Country"
                value={selectedLocation}
                onChange={(e) => setSelectedLocation(e.target.value)}
                data-testid="input-location"
              />
            </div>

            <div>
              <Label htmlFor="type">Job Type</Label>
              <Select value={selectedType} onValueChange={setSelectedType}>
                <SelectTrigger data-testid="select-type">
                  <SelectValue placeholder="Select Type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Types</SelectItem>
                  <SelectItem value="full-time">Full Time</SelectItem>
                  <SelectItem value="part-time">Part Time</SelectItem>
                  <SelectItem value="contract">Contract</SelectItem>
                  <SelectItem value="remote">Remote</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="level">Experience Level</Label>
              <Select value={selectedLevel} onValueChange={setSelectedLevel}>
                <SelectTrigger data-testid="select-level">
                  <SelectValue placeholder="Select Level" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Levels</SelectItem>
                  <SelectItem value="entry">Entry Level</SelectItem>
                  <SelectItem value="mid">Mid Level</SelectItem>
                  <SelectItem value="senior">Senior Level</SelectItem>
                  <SelectItem value="executive">Executive</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Job Listings */}
      <div className="space-y-4">
        {filteredJobs.map((job) => (
          <Card key={job.id} className="hover:shadow-lg transition-shadow">
            <CardContent className="p-6">
              <div className="flex items-start justify-between">
                <div className="flex gap-4 flex-1">
                  {/* Company Logo */}
                  <Avatar className="h-12 w-12">
                    <AvatarImage src={job.companyLogo} />
                    <AvatarFallback className="bg-blue-100 text-blue-800">
                      {job.company[0]}
                    </AvatarFallback>
                  </Avatar>

                  {/* Job Details */}
                  <div className="flex-1 space-y-2">
                    <div className="flex items-start justify-between">
                      <div>
                        <div className="flex items-center gap-2">
                          <h3 className="font-semibold text-lg" data-testid={`text-title-${job.id}`}>
                            {job.title}
                          </h3>
                          {job.isUrgent && (
                            <Badge className="bg-red-500 text-white">Urgent</Badge>
                          )}
                          {job.isRemote && (
                            <Badge variant="secondary">Remote</Badge>
                          )}
                        </div>
                        <p className="text-blue-600 font-medium">{job.company}</p>
                      </div>
                    </div>

                    <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-400">
                      <div className="flex items-center gap-1">
                        <MapPin className="h-4 w-4" />
                        {job.location}
                      </div>
                      <div className="flex items-center gap-1">
                        <Clock className="h-4 w-4" />
                        {formatDistanceToNow(new Date(job.createdAt), { addSuffix: true })}
                      </div>
                      <div className="flex items-center gap-1">
                        <DollarSign className="h-4 w-4" />
                        {job.salaryRange}
                      </div>
                      <div className="flex items-center gap-1">
                        <Users className="h-4 w-4" />
                        {job.applicationsCount} applicants
                      </div>
                    </div>

                    <p className="text-gray-700 dark:text-gray-300 line-clamp-2">
                      {job.description}
                    </p>

                    {/* Skills */}
                    <div className="flex flex-wrap gap-1">
                      {job.skills.slice(0, 5).map((skill, index) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {skill}
                        </Badge>
                      ))}
                      {job.skills.length > 5 && (
                        <Badge variant="outline" className="text-xs">
                          +{job.skills.length - 5} more
                        </Badge>
                      )}
                    </div>

                    {/* Posted By */}
                    <div className="flex items-center gap-2 text-sm">
                      <Avatar className="h-6 w-6">
                        <AvatarImage src={job.postedBy.profileImageUrl} />
                        <AvatarFallback className="text-xs">
                          {job.postedBy.firstName[0]}{job.postedBy.lastName[0]}
                        </AvatarFallback>
                      </Avatar>
                      <span className="text-gray-600 dark:text-gray-400">
                        Posted by {job.postedBy.firstName} {job.postedBy.lastName}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex flex-col gap-2 ml-4">
                  <Button
                    onClick={() => applyMutation.mutate(job.id)}
                    disabled={applyMutation.isPending || hasApplied(job.id)}
                    className={hasApplied(job.id) ? "bg-green-600" : "bg-blue-600 hover:bg-blue-700"}
                    data-testid={`button-apply-${job.id}`}
                  >
                    {hasApplied(job.id) ? "Applied" : applyMutation.isPending ? "Applying..." : "Apply Now"}
                  </Button>
                  <Button variant="outline" size="sm" data-testid={`button-view-${job.id}`}>
                    <ExternalLink className="h-4 w-4 mr-1" />
                    View Details
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredJobs.length === 0 && (
        <Card>
          <CardContent className="p-12 text-center">
            <Briefcase className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="font-semibold text-lg mb-2">No Jobs Found</h3>
            <p className="text-gray-600 dark:text-gray-400">
              Try adjusting your search criteria to find more opportunities
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/matrimony.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Heart, Search, Filter, Star, MapPin, Briefcase, Calendar, Users, Sparkles } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface MatrimonyProfile {
  id: string;
  userId: string;
  firstName: string;
  lastName: string;
  profileImageUrl?: string;
  age: number;
  occupation: string;
  location: string;
  nativePlace: string;
  kulam: string;
  natchathiram: string;
  education: string;
  height: string;
  interests: string[];
  lookingFor: string;
  isActive: boolean;
  matchScore?: number;
  createdAt: string;
}

export default function Matrimony() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState("");
  const [ageRange, setAgeRange] = useState({ min: 18, max: 50 });
  const [selectedKulam, setSelectedKulam] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");

  const { data: profiles = [], isLoading } = useQuery<MatrimonyProfile[]>({
    queryKey: ["/api/matrimony/profiles", searchTerm, ageRange, selectedKulam, selectedLocation],
  });

  const { data: userProfile } = useQuery<MatrimonyProfile>({
    queryKey: ["/api/matrimony/my-profile"],
    enabled: !!user,
  });

  const interestMutation = useMutation({
    mutationFn: async (profileId: string) => {
      await apiRequest(`/api/matrimony/profiles/${profileId}/interest`, {
        method: "POST",
      });
    },
    onSuccess: () => {
      toast({
        title: "Interest Sent",
        description: "Your interest has been sent successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/matrimony/profiles"] });
    },
  });

  const kulamOptions = [
    "Brahmin", "Kshatriya", "Vaishya", "Shudra", "Reddy", "Kamma", "Kapu", "Velama",
    "Naidu", "Chettiar", "Mudaliar", "Pillai", "Nair", "Menon", "Other"
  ];

  const filteredProfiles = profiles.filter(profile => {
    const matchesSearch = !searchTerm || 
      `${profile.firstName} ${profile.lastName}`.toLowerCase().includes(searchTerm.toLowerCase()) ||
      profile.occupation.toLowerCase().includes(searchTerm.toLowerCase()) ||
      profile.location.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesAge = profile.age >= ageRange.min && profile.age <= ageRange.max;
    const matchesKulam = !selectedKulam || profile.kulam === selectedKulam;
    const matchesLocation = !selectedLocation || profile.location.includes(selectedLocation);

    return matchesSearch && matchesAge && matchesKulam && matchesLocation;
  });

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-pink-600"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="text-center space-y-2">
        <div className="flex items-center justify-center gap-2">
          <Heart className="h-8 w-8 text-pink-600" />
          <h1 className="text-3xl font-bold bg-gradient-to-r from-pink-600 to-red-600 bg-clip-text text-transparent">
            Matrimony Portal
          </h1>
        </div>
        <p className="text-gray-600 dark:text-gray-400">
          Find your perfect life partner within our trusted community
        </p>
      </div>

      {/* User Profile Status */}
      {user && (
        <Card className="bg-gradient-to-r from-pink-50 to-red-50 dark:from-pink-900/20 dark:to-red-900/20">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Heart className="h-5 w-5 text-pink-600" />
                <div>
                  <p className="font-medium">Your Matrimony Profile</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {userProfile ? "Profile active and visible" : "Create your profile to get started"}
                  </p>
                </div>
              </div>
              <Button 
                variant={userProfile ? "outline" : "default"}
                className={userProfile ? "" : "bg-pink-600 hover:bg-pink-700"}
                data-testid="button-manage-profile"
              >
                {userProfile ? "Edit Profile" : "Create Profile"}
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Search and Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Search className="h-5 w-5" />
            Search & Filters
          </CardTitle>
          <CardDescription>
            Find compatible matches based on your preferences
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="search">Search</Label>
              <Input
                id="search"
                placeholder="Name, occupation, location..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                data-testid="input-search"
              />
            </div>

            <div>
              <Label htmlFor="kulam">Kulam</Label>
              <Select value={selectedKulam} onValueChange={setSelectedKulam}>
                <SelectTrigger data-testid="select-kulam">
                  <SelectValue placeholder="Select Kulam" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Kulam</SelectItem>
                  {kulamOptions.map(kulam => (
                    <SelectItem key={kulam} value={kulam}>{kulam}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="location">Location</Label>
              <Input
                id="location"
                placeholder="City, State, Country"
                value={selectedLocation}
                onChange={(e) => setSelectedLocation(e.target.value)}
                data-testid="input-location"
              />
            </div>

            <div>
              <Label>Age Range</Label>
              <div className="flex gap-2">
                <Input
                  type="number"
                  placeholder="Min"
                  value={ageRange.min}
                  onChange={(e) => setAgeRange(prev => ({ ...prev, min: parseInt(e.target.value) || 18 }))}
                  className="w-20"
                  data-testid="input-age-min"
                />
                <Input
                  type="number"
                  placeholder="Max"
                  value={ageRange.max}
                  onChange={(e) => setAgeRange(prev => ({ ...prev, max: parseInt(e.target.value) || 50 }))}
                  className="w-20"
                  data-testid="input-age-max"
                />
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Results */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredProfiles.map((profile) => (
          <Card key={profile.id} className="overflow-hidden hover:shadow-lg transition-shadow">
            <CardContent className="p-0">
              {/* Profile Image and Header */}
              <div className="relative bg-gradient-to-br from-pink-100 to-red-100 dark:from-pink-900/30 dark:to-red-900/30 p-6">
                {profile.matchScore && (
                  <div className="absolute top-3 right-3 bg-white dark:bg-gray-800 rounded-full px-2 py-1 text-xs font-semibold flex items-center gap-1">
                    <Sparkles className="h-3 w-3 text-yellow-500" />
                    {profile.matchScore}% Match
                  </div>
                )}

                <div className="text-center">
                  <Avatar className="h-20 w-20 mx-auto mb-3">
                    <AvatarImage src={profile.profileImageUrl} />
                    <AvatarFallback className="text-lg bg-pink-200 text-pink-800">
                      {profile.firstName[0]}{profile.lastName[0]}
                    </AvatarFallback>
                  </Avatar>

                  <h3 className="font-semibold text-lg" data-testid={`text-name-${profile.id}`}>
                    {profile.firstName} {profile.lastName}
                  </h3>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {profile.age} years old
                  </p>
                </div>
              </div>

              {/* Profile Details */}
              <div className="p-4 space-y-3">
                <div className="flex items-center gap-2 text-sm">
                  <Briefcase className="h-4 w-4 text-blue-500" />
                  <span className="font-medium">{profile.occupation}</span>
                </div>

                <div className="flex items-center gap-2 text-sm">
                  <MapPin className="h-4 w-4 text-green-500" />
                  <span>{profile.location}</span>
                </div>

                <div className="grid grid-cols-2 gap-2 text-xs">
                  <div>
                    <span className="font-medium">Kulam:</span> {profile.kulam}
                  </div>
                  <div>
                    <span className="font-medium">Native:</span> {profile.nativePlace}
                  </div>
                  <div>
                    <span className="font-medium">Star:</span> {profile.natchathiram}
                  </div>
                  <div>
                    <span className="font-medium">Height:</span> {profile.height}
                  </div>
                </div>

                {profile.interests && profile.interests.length > 0 && (
                  <div className="flex flex-wrap gap-1">
                    {profile.interests.slice(0, 3).map((interest, index) => (
                      <Badge key={index} variant="secondary" className="text-xs">
                        {interest}
                      </Badge>
                    ))}
                  </div>
                )}

                <div className="flex gap-2 pt-2">
                  <Button
                    onClick={() => interestMutation.mutate(profile.id)}
                    disabled={interestMutation.isPending}
                    className="flex-1 bg-pink-600 hover:bg-pink-700"
                    size="sm"
                    data-testid={`button-interest-${profile.id}`}
                  >
                    <Heart className="h-4 w-4 mr-1" />
                    {interestMutation.isPending ? "Sending..." : "Express Interest"}
                  </Button>
                  <Button variant="outline" size="sm" data-testid={`button-view-${profile.id}`}>
                    View Profile
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {filteredProfiles.length === 0 && (
        <Card>
          <CardContent className="p-12 text-center">
            <Heart className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="font-semibold text-lg mb-2">No Profiles Found</h3>
            <p className="text-gray-600 dark:text-gray-400">
              Try adjusting your search criteria to find more matches
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/members.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Header from "@/components/header";
import Navigation from "@/components/navigation";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Search, MapPin, Briefcase, UserPlus, Users, Filter } from "lucide-react";
import type { User } from "@shared/schema";

export default function Members() {
  const { toast } = useToast();
  const { user, isAuthenticated, isLoading: authLoading } = useAuth();
  const [searchQuery, setSearchQuery] = useState("");
  const [debouncedQuery, setDebouncedQuery] = useState("");

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!authLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, authLoading, toast]);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchQuery]);

  // Fetch members
  const { data: members, isLoading: membersLoading } = useQuery<User[]>({
    queryKey: ["/api/users/search", { q: debouncedQuery, limit: "20" }],
    enabled: !!debouncedQuery && debouncedQuery.length >= 2,
    retry: false,
  });

  if (authLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
          <p className="mt-4 text-gray-600">Loading members...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />
      <Navigation />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Community Members</h1>
          <p className="text-gray-600">Connect with fellow community members worldwide</p>
        </div>

        {/* Search Bar */}
        <Card className="mb-8">
          <CardContent className="p-6">
            <div className="flex items-center space-x-4">
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <Input
                  placeholder="Search by name, location, profession, or native place..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                  data-testid="input-search"
                />
              </div>
              <Button variant="outline" data-testid="button-filter">
                <Filter size={16} className="mr-2" />
                Filters
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Search Results */}
        {searchQuery.length >= 2 ? (
          <div>
            {membersLoading ? (
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(6)].map((_, i) => (
                  <Card key={i}>
                    <CardContent className="p-6">
                      <div className="flex items-start space-x-4">
                        <Skeleton className="w-16 h-16 rounded-full" />
                        <div className="flex-1 space-y-2">
                          <Skeleton className="h-5 w-32" />
                          <Skeleton className="h-4 w-24" />
                          <Skeleton className="h-4 w-20" />
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : members && members.length > 0 ? (
              <div>
                <div className="mb-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">
                    Search Results ({members.length})
                  </h2>
                  <p className="text-gray-600">Found {members.length} members matching "{debouncedQuery}"</p>
                </div>

                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {members.map((member) => (
                    <Card key={member.id} className="hover:shadow-lg transition-shadow">
                      <CardContent className="p-6">
                        <div className="flex items-start space-x-4 mb-4">
                          <Avatar className="h-16 w-16">
                            <AvatarImage src={member.profileImageUrl || ""} alt={`${member.firstName} ${member.lastName}`} />
                            <AvatarFallback className="bg-primary-500 text-white">
                              {(member.firstName?.[0] || "") + (member.lastName?.[0] || "")}
                            </AvatarFallback>
                          </Avatar>
                          <div className="flex-1 min-w-0">
                            <h3 className="font-semibold text-gray-900 truncate" data-testid={`text-name-${member.id}`}>
                              {member.firstName} {member.lastName}
                            </h3>
                            {member.location && (
                              <div className="flex items-center space-x-1 text-gray-600 mb-1">
                                <MapPin size={14} />
                                <span className="text-sm truncate" data-testid={`text-location-${member.id}`}>
                                  {member.location}
                                </span>
                              </div>
                            )}
                            {member.occupation && (
                              <div className="flex items-center space-x-1 text-gray-600 mb-2">
                                <Briefcase size={14} />
                                <span className="text-sm truncate" data-testid={`text-occupation-${member.id}`}>
                                  {member.occupation}
                                </span>
                              </div>
                            )}
                            <div className="flex items-center space-x-2">
                              <Badge variant="outline" className="text-xs capitalize">
                                {member.role}
                              </Badge>
                              {member.isVerified && (
                                <Badge variant="default" className="text-xs bg-green-500">
                                  Verified
                                </Badge>
                              )}
                            </div>
                          </div>
                        </div>

                        {member.nativePlace && (
                          <div className="mb-4">
                            <p className="text-sm text-gray-600">
                              <strong>Native Place:</strong> {member.nativePlace}
                            </p>
                          </div>
                        )}

                        {member.aboutMe && (
                          <div className="mb-4">
                            <p className="text-sm text-gray-700 line-clamp-2">
                              {member.aboutMe}
                            </p>
                          </div>
                        )}

                        <div className="flex justify-between items-center pt-4 border-t border-gray-100">
                          <Button 
                            size="sm" 
                            className="bg-primary-500 hover:bg-primary-600"
                            data-testid={`button-connect-${member.id}`}
                          >
                            <UserPlus size={14} className="mr-1" />
                            Connect
                          </Button>
                          <Button variant="outline" size="sm" data-testid={`button-view-${member.id}`}>
                            View Profile
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            ) : (
              <Card>
                <CardContent className="p-12 text-center">
                  <Users className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No members found</h3>
                  <p className="text-gray-600 mb-4">
                    No members match your search for "{debouncedQuery}". Try different keywords.
                  </p>
                  <Button 
                    variant="outline"
                    onClick={() => setSearchQuery("")}
                    data-testid="button-clear-search"
                  >
                    Clear Search
                  </Button>
                </CardContent>
              </Card>
            )}
          </div>
        ) : (
          /* Default State */
          <div className="text-center py-16">
            <div className="mx-auto w-24 h-24 bg-primary-50 rounded-full flex items-center justify-center mb-6">
              <Users className="h-12 w-12 text-primary-500" />
            </div>
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              Discover Community Members
            </h2>
            <p className="text-gray-600 max-w-2xl mx-auto mb-8">
              Start searching to find and connect with fellow community members. You can search by name, location, profession, native place, and more.
            </p>
            <div className="bg-white rounded-xl p-8 max-w-md mx-auto border border-gray-200">
              <h3 className="font-semibold text-gray-900 mb-4">Search Tips:</h3>
              <ul className="text-sm text-gray-600 space-y-2 text-left">
                <li>‚Ä¢ Search by name (first or last name)</li>
                <li>‚Ä¢ Find members by current location</li>
                <li>‚Ä¢ Discover people in your profession</li>
                <li>‚Ä¢ Connect with those from your native place</li>
                <li>‚Ä¢ Look for specific skills or interests</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

